import React, { useReducer, useEffect, useMemo, useCallback } from 'react';
import { useLocalStorage } from '../hooks/useLocalStorage';
import { taskReducer, initialTaskState } from '../reducers/taskReducer';

const Tasks = () => {
  const [storedTasks, setStoredTasks] = useLocalStorage('tasks', []);
  const [state, dispatch] = useReducer(taskReducer, {
    ...initialTaskState,
    tasks: storedTasks
  });

  const [newTask, setNewTask] = React.useState('');

  // Persist tasks to localStorage whenever they change
  useEffect(() => {
    setStoredTasks(state.tasks);
  }, [state.tasks, setStoredTasks]);

  // Memoized filtered tasks
  const filteredTasks = useMemo(() => {
    switch (state.filter) {
      case 'active':
        return state.tasks.filter(task => !task.completed);
      case 'completed':
        return state.tasks.filter(task => task.completed);
      default:
        return state.tasks;
    }
  }, [state.tasks, state.filter]);

  // Memoized task statistics
  const taskStats = useMemo(() => {
    const total = state.tasks.length;
    const completed = state.tasks.filter(task => task.completed).length;
    const active = total - completed;
    return { total, completed, active };
  }, [state.tasks]);

  // Memoized handlers
  const addTask = useCallback((e) => {
    e.preventDefault();
    if (newTask.trim()) {
      dispatch({
        type: 'ADD_TASK',
        payload: {
          title: newTask.trim(),
          completed: false,
          createdAt: new Date().toISOString()
        }
      });
      setNewTask('');
    }
  }, [newTask]);

  const toggleTask = useCallback((id) => {
    dispatch({ type: 'TOGGLE_TASK', payload: id });
  }, []);

  const deleteTask = useCallback((id) => {
    dispatch({ type: 'DELETE_TASK', payload: id });
  }, []);

  const updateTask = useCallback((id, updates) => {
    dispatch({ type: 'UPDATE_TASK', payload: { id, ...updates } });
  }, []);

  const setFilter = useCallback((filter) => {
    dispatch({ type: 'SET_FILTER', payload: filter });
  }, []);

  return (
    <div className="tasks">
      <h2>Task Management</h2>
      
      <div className="task-stats">
        <div className="stat-item">
          <span className="stat-number">{taskStats.total}</span>
          <span className="stat-label">Total</span>
        </div>
        <div className="stat-item">
          <span className="stat-number">{taskStats.active}</span>
          <span className="stat-label">Active</span>
        </div>
        <div className="stat-item">
          <span className="stat-number">{taskStats.completed}</span>
          <span className="stat-label">Completed</span>
        </div>
      </div>

      <form onSubmit={addTask} className="add-task-form">
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Add a new task..."
          className="task-input"
        />
        <button type="submit" className="add-task-btn">Add Task</button>
      </form>

      <div className="filter-buttons">
        <button
          onClick={() => setFilter('all')}
          className={state.filter === 'all' ? 'active' : ''}
        >
          All
        </button>
        <button
          onClick={() => setFilter('active')}
          className={state.filter === 'active' ? 'active' : ''}
        >
          Active
        </button>
        <button
          onClick={() => setFilter('completed')}
          className={state.filter === 'completed' ? 'active' : ''}
        >
          Completed
        </button>
      </div>

      <div className="tasks-list">
        {filteredTasks.length === 0 ? (
          <p className="no-tasks">No tasks found.</p>
        ) : (
          filteredTasks.map(task => (
            <div key={task.id} className={`task-item ${task.completed ? 'completed' : ''}`}>
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => toggleTask(task.id)}
                className="task-checkbox"
              />
              <span className="task-title">{task.title}</span>
              <div className="task-actions">
                <button
                  onClick={() => updateTask(task.id, { title: prompt('Edit task:', task.title) })}
                  className="edit-btn"
                >
                  ‚úèÔ∏è
                </button>
                <button
                  onClick={() => deleteTask(task.id)}
                  className="delete-btn"
                >
                  üóëÔ∏è
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Tasks;
