import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';

const Profile = () => {
  const { user, updateUser } = useAuth();
  const { isDark, setTheme } = useTheme();
  const [isEditing, setIsEditing] = useState(false);
  const [editForm, setEditForm] = useState({
    name: user?.name || '',
    email: user?.email || '',
    bio: user?.bio || ''
  });

  const handleEditSubmit = (e) => {
    e.preventDefault();
    updateUser(editForm);
    setIsEditing(false);
  };

  const handleInputChange = (e) => {
    setEditForm({
      ...editForm,
      [e.target.name]: e.target.value
    });
  };

  const handleThemeToggle = () => {
    setTheme(!isDark);
  };

  return (
    <div className="profile">
      <h2>User Profile</h2>
      <p>This component is lazy loaded and uses Context API for state management!</p>
      
      <div className="profile-card">
        <div className="profile-header">
          <div className="profile-avatar">
            {user?.name?.charAt(0)?.toUpperCase() || 'U'}
          </div>
          <div className="profile-info">
            <h3>{user?.name || 'User'}</h3>
            <p>{user?.email || 'user@example.com'}</p>
            {user?.bio && <p className="profile-bio">{user.bio}</p>}
          </div>
        </div>

        {!isEditing ? (
          <div className="profile-actions">
            <button
              onClick={() => setIsEditing(true)}
              className="edit-profile-btn"
            >
              ‚úèÔ∏è Edit Profile
            </button>
          </div>
        ) : (
          <form onSubmit={handleEditSubmit} className="edit-profile-form">
            <div className="form-group">
              <label htmlFor="name">Name:</label>
              <input
                type="text"
                id="name"
                name="name"
                value={editForm.name}
                onChange={handleInputChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label htmlFor="email">Email:</label>
              <input
                type="email"
                id="email"
                name="email"
                value={editForm.email}
                onChange={handleInputChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label htmlFor="bio">Bio:</label>
              <textarea
                id="bio"
                name="bio"
                value={editForm.bio}
                onChange={handleInputChange}
                rows="3"
                placeholder="Tell us about yourself..."
              />
            </div>
            
            <div className="form-actions">
              <button type="submit" className="save-btn">
                üíæ Save Changes
              </button>
              <button
                type="button"
                onClick={() => setIsEditing(false)}
                className="cancel-btn"
              >
                ‚ùå Cancel
              </button>
            </div>
          </form>
        )}
      </div>

      <div className="preferences-section">
        <h3>Preferences</h3>
        
        <div className="preference-item">
          <label>Theme:</label>
          <div className="theme-controls">
            <button
              onClick={() => setTheme(false)}
              className={`theme-btn ${!isDark ? 'active' : ''}`}
            >
              ‚òÄÔ∏è Light
            </button>
            <button
              onClick={() => setTheme(true)}
              className={`theme-btn ${isDark ? 'active' : ''}`}
            >
              üåô Dark
            </button>
          </div>
        </div>
      </div>

      <div className="context-api-info">
        <h3>Context API Demo</h3>
        <p>
          This Profile component demonstrates how Context API works:
        </p>
        <ul>
          <li>Uses useAuth() hook to access user data</li>
          <li>Uses useTheme() hook to manage theme preferences</li>
          <li>Updates are automatically reflected across the app</li>
          <li>No prop drilling needed</li>
        </ul>
      </div>

      <div className="lazy-loading-info">
        <h3>Lazy Loading Demo</h3>
        <p>
          This Profile component is lazy loaded:
        </p>
        <ul>
          <li>Loaded only when user navigates to /profile</li>
          <li>Reduces initial bundle size</li>
          <li>Shows loading spinner while loading</li>
        </ul>
      </div>
    </div>
  );
};

export default Profile;
