import React, { useState, useMemo, useCallback, useEffect } from 'react';
import { useLocalStorage } from '../hooks/useLocalStorage';

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [storedNotes, setStoredNotes] = useLocalStorage('notes', []);

  // Load notes from localStorage on component mount
  useEffect(() => {
    setNotes(storedNotes);
  }, [storedNotes]);

  // Save notes to localStorage whenever they change
  useEffect(() => {
    setStoredNotes(notes);
  }, [notes, setStoredNotes]);

  // Memoized filtered notes based on search and category
  const filteredNotes = useMemo(() => {
    return notes.filter(note => {
      const matchesSearch = note.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           note.content.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesCategory = selectedCategory === 'all' || note.category === selectedCategory;
      return matchesSearch && matchesCategory;
    });
  }, [notes, searchTerm, selectedCategory]);

  // Memoized categories from existing notes
  const categories = useMemo(() => {
    const uniqueCategories = [...new Set(notes.map(note => note.category))];
    return ['all', ...uniqueCategories];
  }, [notes]);

  // Memoized note statistics
  const noteStats = useMemo(() => {
    const total = notes.length;
    const byCategory = notes.reduce((acc, note) => {
      acc[note.category] = (acc[note.category] || 0) + 1;
      return acc;
    }, {});
    return { total, byCategory };
  }, [notes]);

  // Memoized handlers to prevent unnecessary re-renders
  const addNote = useCallback((e) => {
    e.preventDefault();
    if (newNote.trim()) {
      const note = {
        id: Date.now(),
        title: newNote.trim(),
        content: '',
        category: 'general',
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      };
      setNotes(prev => [note, ...prev]);
      setNewNote('');
    }
  }, [newNote]);

  const updateNote = useCallback((id, updates) => {
    setNotes(prev => prev.map(note =>
      note.id === id
        ? { ...note, ...updates, updatedAt: new Date().toISOString() }
        : note
    ));
  }, []);

  const deleteNote = useCallback((id) => {
    setNotes(prev => prev.filter(note => note.id !== id));
  }, []);

  const handleSearchChange = useCallback((e) => {
    setSearchTerm(e.target.value);
  }, []);

  const handleCategoryChange = useCallback((e) => {
    setSelectedCategory(e.target.value);
  }, []);

  return (
    <div className="notes">
      <h2>Notes App</h2>
      
      <div className="notes-stats">
        <div className="stat-item">
          <span className="stat-number">{noteStats.total}</span>
          <span className="stat-label">Total Notes</span>
        </div>
        {Object.entries(noteStats.byCategory).map(([category, count]) => (
          <div key={category} className="stat-item">
            <span className="stat-number">{count}</span>
            <span className="stat-label">{category}</span>
          </div>
        ))}
      </div>

      <form onSubmit={addNote} className="add-note-form">
        <input
          type="text"
          value={newNote}
          onChange={(e) => setNewNote(e.target.value)}
          placeholder="Add a new note..."
          className="note-input"
        />
        <button type="submit" className="add-note-btn">Add Note</button>
      </form>

      <div className="notes-controls">
        <input
          type="text"
          value={searchTerm}
          onChange={handleSearchChange}
          placeholder="Search notes..."
          className="search-input"
        />
        
        <select
          value={selectedCategory}
          onChange={handleCategoryChange}
          className="category-select"
        >
          {categories.map(category => (
            <option key={category} value={category}>
              {category === 'all' ? 'All Categories' : category}
            </option>
          ))}
        </select>
      </div>

      <div className="notes-list">
        {filteredNotes.length === 0 ? (
          <p className="no-notes">No notes found.</p>
        ) : (
          filteredNotes.map(note => (
            <div key={note.id} className="note-item">
              <div className="note-header">
                <input
                  type="text"
                  value={note.title}
                  onChange={(e) => updateNote(note.id, { title: e.target.value })}
                  className="note-title-input"
                />
                <select
                  value={note.category}
                  onChange={(e) => updateNote(note.id, { category: e.target.value })}
                  className="note-category-select"
                >
                  <option value="general">General</option>
                  <option value="work">Work</option>
                  <option value="personal">Personal</option>
                  <option value="ideas">Ideas</option>
                </select>
              </div>
              
              <textarea
                value={note.content}
                onChange={(e) => updateNote(note.id, { content: e.target.value })}
                placeholder="Write your note content here..."
                className="note-content"
                rows="4"
              />
              
              <div className="note-footer">
                <span className="note-date">
                  Updated: {new Date(note.updatedAt).toLocaleDateString()}
                </span>
                <button
                  onClick={() => deleteNote(note.id)}
                  className="delete-note-btn"
                >
                  üóëÔ∏è Delete
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Notes;
